import json
import lightspark_crypto as lsc
import unittest

class Validator(lsc.Validation):
    def should_sign(self, webhook):
        return True

class TestFFI(unittest.TestCase):
    def test_handler(self):
        data_hex = ""
        data = bytes.fromhex(data_hex)
        sig = "a64c69f1266bc1dc1322c3f40eba7ba2d536c714774a4fc04f0938609482f5d9"
        sec = "39kyJO140v7fYkwHnR7jz8Y3UphqVeNYQk44Xx049ws"
        seed = "1a6deac8f74fb2e332677e3f4833b5e962f80d153fb368b8ee322a9caca4113d56cccd88f1c6a74e152669d8cd373fee2f27e3645d80de27640177a8c71395f8"
        seed_bytes = bytes.fromhex(seed)
        validator = Validator()
        res = lsc.handle_remote_signing_webhook_event(data, sig, sec, seed_bytes, validator)
        variables = json.loads(res.variables)
        assert variables["node_id"] == "node_with_server_signing:018a9635-3673-88df-0000-827f23051b19"
        assert variables["shared_secret"] == "85ae1d8a548e2f96667dc8f63e7201ed16fb1bc7248c7aae6ea6fb76d244f74b"